async function getAllMails(i,n) {
  dbRef.child("mails").child(i).child("mails").child(n).get().then((snapshot) => {
    if (snapshot.exists()) {
      mails = snapshot.val();
      console.log(mails);
      loadMails();
    } else {
      alert("No Internet Connection");
    }
  }).catch((error) => {
    console.error(error);
  });
}

function loadMails() {
  mailBox.innerHTML=`
  <div class="mailDiv">
  <label>from ${mails.user} to you</label>
  <div class="mailTitle">${mails.title}</div>
  <div class="mailBody">${mails.body}</div>
  </div>
`;

}
